    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        Representante representante = new Representante();
        Ctrl_Representante controlRep = new Ctrl_Representante();

        //validamos basio
        if (txtnombre1.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "tiene que ingresar un nombre y cedula");

        } else {

            if (!controlRep.exiteRep(cedularep.getText().trim())) {
                representante.setPrimernombre(txtnombre1.getText().trim());
                representante.setSegundonombre(nombre2.getText().trim());
                representante.setPrimerapellido(apellido1.getText().trim());
                representante.setSegundoapellido(apellido2.getText().trim());
                representante.setCedula(cedularep.getText().trim());
                representante.setTelefono(telefono.getText().trim());
                representante.setDireccion(direccion.getText().trim());

                if (controlRep.ingresar(representante)) {
                    JOptionPane.showMessageDialog(null, "registro exitoso");
                } else {
                    JOptionPane.showMessageDialog(null, "Error al guardar");
                }
            } else {
                JOptionPane.showMessageDialog(null, "Representante ya registrado");
            }

        }
        //limpiar campo
        txtnombre1.setText("");
        nombre2.setText("");
        apellido1.setText("");
        apellido2.setText("");
        cedularep.setText("");
        telefono.setText("");
        direccion.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

